import React, { Fragment, useState } from "react";
import "../../App.css";
import { Link } from "react-router-dom";


const Header = () => {
  const [isOpen, setIsOpen] = useState(false);
  const [sidebarOpen, setSidebarOpen] = useState(false);

  return (
    <Fragment>
      <div className="header">
        {/* Menu button */}
        <div className="menu">
          <button
            id="menu"
            onClick={() => {
              console.log("Menu button clicked!"); // Debugging log
              setSidebarOpen(true);
            }}
          >
            <img src="/images/menu.svg" alt="menu" />
          </button>
        </div>

        {/* Logo */}
        <div className="logo">
          <Link to="/">
            <img src="/images/logo.svg" alt="logo" />
          </Link>
        </div>

        {/* Navbar */}
        <div className="main-nav">
          <div className="navbar">
            <Link to="/">Home</Link>
            <Link to="/study-material">Study Material</Link>
            <Link to="/hall-of-achiver">Hall of Achievers</Link>
            <Link to="/about">About</Link>
          </div>
        </div>

        {/* Sidebar */}
        <div className={`sidebar ${sidebarOpen ? "active" : ""}`}>
          {" "}
          {/* ✅ Toggle sidebar */}
          <button id="cancel" onClick={() => setSidebarOpen(false)}>
            {" "}
            {/* ✅ Close sidebar */}
            <img src="/images/cancel.svg" alt="cancel" />
          </button>
          <Link to="/">Home</Link>
          <Link to="/study-material">Study Material</Link>
          <Link to="/hall-of-achiver">Hall of Achievers</Link>
          <Link to="/about">About</Link>
        </div>

        {/* Login Section */}
        <div className="login-header">
          {loading ? (
            <p>Loading...</p> // ✅ Show loading state
          ) : user ? (
            <div
              className="user-menu"
              onMouseEnter={() => setIsOpen(true)}
              onMouseLeave={() => setIsOpen(false)} // ✅ Close on mouse leave
            >
              <button className="user-button">
                <img src="images/user.svg" alt="user" />
              </button>
              {isOpen && (
                <div className="submenu">
                  <Link to="/profile">User Profile</Link>
                  <button onClick={logoutHandler} className="logout-button">
                    Logout
                  </button>
                </div>
              )}
            </div>
          ) : (
            <Link to="/login">
              <button className="login-button">Login</button>
            </Link>
          )}
        </div>
      </div>
    </Fragment>
  );
};

export default Header;
